    /**
     * 组合排行榜
     * @param ranks
     */
    public static List<UserRankView> getRacingRank(List<UserRankView> ranks){
        List<UserRankView> all = JSONUtil.jsonToObject(new TypeReference<List<UserRankView>>(){}, initRank);
        int index = 0;
        for (UserRankView v : all){
            int r = 0;
            if(index >= 0 && index < 10){
                r = new Random().nextInt(10000) + 100000;
            }else if(index >= 10 && index < 40){
                r = new Random().nextInt(10000) + 50000;
            }else if(index >= 40 && index < 70){
                r = new Random().nextInt(10000) + 30000;
            }else if(index >= 70 && index < 100){
                r = new Random().nextInt(10000) + 20000;
            }
            v.setRank(r);
        }
        if(CollectionUtils.isNotEmpty(ranks)){
            all.addAll(ranks);
            Collections.sort(all);
            //取前100
            return all.subList(0, 100);
        }
        Collections.sort(all);
        return all;
    }

